import {EventEmitter} from "events";

class Model extends EventEmitter{
     constructor(){
         super();
         this.state={
             questions: [{
                 id: "0",
                 title: "Blender3D",
                 question: "What does it do?",
                 author: "Ines",
                 date: "01/02/2013",
                 tags: "Blender"

             }, {
                 id:"1",
                 title: "Kittens",
                 question: "Are they fluffy?",
                 author: "Maria",
                 date:"23/04/2011",
                 tags: "Pets"
                }],

             newQuestion: [{
                 id: "",
                 title: "",
                 question: "",
                 author: "",
                 date: "",
                 tags: ""
             }]
         };
     }
     addQuestion(title, question, author, date, tags){
         this.state={
            ...this.state,
             auxId: ++this.state.auxId,
            questions:this.state.questions.concat([{
                id:this.state.auxId,
                title: title,
                question: question,
                author: author,
                date: date,
                tags: tags
            }])
         };
         this.emit("change", this.state);

     }

     changeNewQuestionProperty(property, value){
         this.state= {
             ...this.state,
             newQuestion:{
                 ...this.state.newQuestion,
                 [property]: value
             }
         };
         this.emit("change", this.state);
     }

     filterByTitle(){
         debugger;
         const result=this.state.questions.filter(post=>post.title===this.state.toSearch)
         this.state = {
             ...this.state,
             searchQuestions: result
         };
     }
}
const model = new Model();
export default model;
