import React, { Component } from "react";
import model from "../model/model";

import CreateQuestion from "./CreateQuestion";
import createQuestionPresenter from "../presenter/CreateQuestionPresenter";
import questionsList from "./QuestionsList";

const mapModelStateToComponentState = model => ({
    questions: model.questions,
    title:  model.newQuestion.title,
    question:  model.newQuestion.question,
    date:  model.newQuestion.date,
    tags:  model.newQuestion.tags//sa nu dea eroare aici
});

export default class SmartCreateQuestion extends Component {
    constructor() {
        super();
        this.state = mapModelStateToComponentState(model.state);
        this.listener = model => this.setState(mapModelStateToComponentState(model));
        model.addListener("change", this.listener);
    }

    componentWillUnmount() {
        model.removeListener("change", this.listener);
    }

    render() {
        return (
            <CreateQuestion
                onCreateQuestion={createQuestionPresenter.onCreateQuestion}
                onChangeQuestion={createQuestionPresenter.onChangeQuestion}
               /* titleInput={this.state.titleInput}*/
                title={this.state.title}
                question={this.state.question}
                author={this.state.author}
                date={this.state.date}
                tags={this.state.tags}/>
        );
    }
}
