import React,{Component} from "react";
import model from "../model/model"
import QuestionsList from "./QuestionsList";
import QuestionsListPresenter from "../presenter/StudentsListPresenter";
const mapModelStateToComponentState = modelState => ({
    questions: modelState.questions,
    titleInput:  modelState.newQuestion.titleInput,
    questionInput:  modelState.newQuestion.questionInput, //sa nu dea eroare aici
});

export default class SmartQuestionsList extends Component{
    constructor(){
        super();
        this.state = mapModelStateToComponentState(model.state);
        this.listener = modelState => this.setState(mapModelStateToComponentState(modelState));
        model.addListener("change", this.listener);
    }
    componentWillUnmount() {
        model.removeListener("change", this.listener);
    }
    render(){
        return(
            <QuestionsList
                titleInput={this.state.titleInput}
                questionInput={this.state.questionInput}
                onCreate={QuestionsListPresenter.onCreate}
                onChange={QuestionsListPresenter.onChange}

                title= "Questions"
                questions={this.state.questions}/>

        );
    }
}
