import React from "react";

const CreateQuestion = ({ title, question, author, date, tags , onCreateQuestion, onChangeQuestion}) => (
    <div>
        <h2>Add Question</h2>
        <div>
            <label>Title: </label>
            <input value={title}
                   onChangeQuestion={ e => onChangeQuestion("title", e.target.value) } />
            <br />
            <label>Question: </label>
            <input value={question}
                   onChangeQuestion={ e => onChangeQuestion("question", e.target.value) } />
            <br />
            <label>Author: </label>
            <input value={author}
                   onChangeQuestion={ e => onChangeQuestion("author", e.target.value) } />
            <br />
            <label>Date: </label>
            <input value={date}
                   onChangeQuestion={ e => onChangeQuestion("date", e.target.value) } />
            <br />
            <label>Tag: </label>
            <input value={tags}
                   onChangeQuestion={ e => onChangeQuestion("tags", e.target.value) } />
            <br />
            <button onClick={onCreateQuestion}>Create!</button>
        </div>
    </div>
);

export default CreateQuestion;
